pragma solidity 0.5.0;
import "openzeppelin-solidity/contracts/ownership/Ownable.sol";

contract MyLoomLottery is Ownable {
  address payable [] public players;
  uint private randNonce;
  event NewTicket(address buyer);
  event NewWinner(address winner, uint amount);

  function buyTicket() external payable {
    require(msg.value == 0.01 ether);
    players.push(msg.sender);
    emit NewTicket(msg.sender);
  }
  function selectWinner() public onlyOwner {
   uint random = uint(keccak256(abi.encodePacked(now, msg.sender, randNonce))) % players.length;
   address payable winner = players[random];
   players = new address payable [](0);
   uint amount = address(this).balance;
   emit NewWinner(winner, amount);
   winner.transfer(amount);
  }
}
